import { ADD_MOVEMENT } from '../actions/constants.js';

const initialState = {
    name: '',
    weight: null  
};

const addMovementReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT:
            return { ...state, name: state.name, ...action.name, weight: state.weight, ...action.weight }
        default:
            return state;
    }
};

export default addMovementReducer;
