import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    PREntry: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        fontFamily: 'PT Sans Caption',
        fontSize: '18px',
        marginTop: '100px'
    },
    PRvalue: {
        margin: '10px',
        width: '200px',
    },
    PRChart: {
        background: '#C4C4C4',
        borderRadius: '10px',
        borderCollapse: 'separate',
        borderSpacing: '10px',
        display: 'flex',
        flexDirection: 'column',
    },
    percentHeader: {
        borderRadius: '10px',
        padding: '5px',
        fontSize: '24px', 
        display: 'flex',
        justifyContent: 'space-around'
    },
    percentBox: {
        background: '#E7E7E7',
        border: '1px solid #C4C4C4',
        padding: '5px',
        fontSize: '24px',
        display: 'flex',
        justifyContent: 'space-around'
    },
    notFoundPage: {
        display: 'block',
        fontSize: '30px',
        background: '#C4C4C4',
        padding: '30px',
        boxShadow: '0px 2px 2px #A9A9A9',
        width: '100%'
    },
    notFoundPageClickHere: {
        fontSize: '15px',
    },
    loadingPage: {
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        background: '#C4C4C4',
        boxShadow: '0px 2px 2px #A9A9A9',
        paddingTop: '10px'
    },
    loadingPageText:{
        fontSize: '36px',
        fontFamily: 'PT Sans Caption',
        padding: '30px'
    },
}));

const MovementPage = (props) => {
    const classes = useStyles();
    const weights = props.weight;
    console.log(weights);

    let [pr, setPr] = useState('');
    const [results, setResults] = useState([]);

    useEffect(() => {
        const arr = [];
        let percentage = 100;
        while (percentage > 50 ) {
            arr.push([percentage, (pr * percentage) / 100]);
            percentage -= 5;
        }
        setResults(arr);
    }, [pr]);

    return (
        <div>
            <Header title={"Movement Page"} />
            <div>
                <div controlId="formMovement" className={classes.PREntry}>
                    <div>PR:</div>
                    <div
                        type="number"
                        placeholder="Enter current PR"
                        value={pr}
                        onChange={(e) => setPr(e.target.value)}
                        className={classes.PRvalue}
                    />
                </div>   
                <div className={classes.PRChart}>
                    <h1 className={classes.percentHeader} >Percent<div>Weight</div></h1>

                    <div>
                        {results.map((result) => (
                        <div key={result[0]}>
                            <div className={classes.percentBox}>{result[0]}% <div>{result[1]}</div></div>

                        </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    console.log(state.move[0].newMovement, "map"); 
    return {
        name: state.move,
        weight: state.move
    }
};

 export default connect(mapStateToProps)(MovementPage);
