import { connect } from 'react-redux';
import history from '../history';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import { fetchMovements } from '../actions/index';;

const useStyles = makeStyles((theme) => ({
    movementButtons: {
        fontFamily: 'PT Sans Caption',
        marginTop: '30px',
        borderRadius: '10px',
        background: '#C4C4C4',
        boxShadow: '0px 2px 2px #A9A9A9',
        width: '400px',
        display: 'flex',
        flexDirection: 'column',
    },
    noMovementsMessage: {
        marginTop: '30px',
        fontFamily: 'PT Sans Caption',
        fontSize: '20px',
        textTransform: 'uppercase',
    },
    movementButtonsDiv: {
        marginTop: '100px'
    },
}));

const MovementButton = (props) => {
    const classes = useStyles();
    const displayMovements = () => {
        if (!props.formValues) {
            return "Loading..."
        } else {
            console.log(props.formValues, "home");
            // return props.formValues.map((movement) => {
            //     <Button 
            //     key={movement.movementName}
            //     className={classes.movementButtons} 
            //     onClick={() => history.push(`/movement/${movement.id}/${movement.movementName}/${movement.movementWeight}`)}
            //     >
            //     {movement.movementName} - {movement.movementWeight}lbs
            //      </Button>
            // })
        }
    };
    const mapNames = props.formValues.map((movement) => {
        if (!movement) {
            return "Loading..."
        } else {
        return (
            <Button 
                key={movement.movementName}
                className={classes.movementButtons} 
                onClick={() => history.push(`/movement/${movement.id}/${movement.movementName}/${movement.movementWeight}`)}
            >
                {movement.movementName} - {movement.movementWeight}lbs
            </Button>
        )}
    });

    const displayMovementButtons = () => {
        if (mapNames.length === 0) {    
            return <div className={classes.noMovementsMessage} >Click add button to begin</div> 
        };

        return <div>{mapNames}<div>{displayMovements()}</div></div>
    };

    return <div className={classes.movementButtonsDiv}>{displayMovementButtons()}</div>
};

const mapStateToProps = (state) => {
    return {
        formValues: state.move,
    }
};

const mapDispatchToProps = (dispatch) => {
    return({
        fetchMovements: (formValues) => dispatch(fetchMovements(formValues)),
    })
};

export default connect(mapStateToProps, mapDispatchToProps)(MovementButton);
