import React from 'react';
import Header from '../components/Header';
import { makeStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import { addMovement } from '../actions/index';
import { Field, reduxForm } from 'redux-form';

const useStyles = makeStyles(() => ({
    addPage: {
        display: 'flex',
        justifyContent: 'center',
        marginTop: '100px'
    },
    addMovementDiv: {
        background: '#C4C4C4',
        fontFamily: 'PT Sans Caption',
        fontSize: '18px',
        borderRadius: '10px',
        padding: '20px',
        marginTop: '50px',
        display: 'flex',
        flexDirection: 'column',
        textAlign: 'right',
      },
      movementName: {
        padding: '10px',
        margin: '10px',
      },
      textBoxColor: {
        background: 'white',
        borderRadius: '10px',
      },
      movementWeight: {
        margin: '10px',
        padding: '10px',
      },
      addButton: {
          width: '50px',
          borderRadius: '10px',
          marginLeft: '95px'
      },
}));

const renderInput = ({ input, label, meta }) => {
    return (
        <div>
            <label>{label}:   </label>
            <input {...input} /> 
            {renderError(meta)}
        </div>    
    )  
};
const renderError = ({ error, touched }) => {
    if (touched && error ) {
        return (
            <div>
                {error}
            </div>
        )
    };
};

const AddPage = (props) => {
    const classes = useStyles();
    const onSubmit = (formValues) => {
        props.addMovement(formValues);
    };
    const onChange = (event) => {
        event.preventDefault();
        props.setFormValues({
            ...props.formValues
        })
    };

    return (
        <div>
            <Header title="Add Page" />
            <div className={classes.addPage}>
                <div className={classes.addMovementDiv}>
                    <form onSubmit={props.handleSubmit(onSubmit)} onChange={props.handleChange(onChange)} >
                        <Field 
                            name="movementName" 
                            component={renderInput} 
                            label="Movement Name" 
                        />
                        <Field
                            name="movementWeight" 
                            component={renderInput} 
                            label="One Rep Max" 
                         />
                         <button >Submit</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (props) => {
    return {
        name: props.move.name,
        weight: props.move.weight,   
    }   
};

const mapDispatchToProps = (dispatch) => {
    return({
        addMovement: (name, weight) => dispatch(addMovement(name, weight)),
    })

};

const validate = (formValues) => {
    const errors = {};

    if (!formValues.movementName) {
        errors.movementName = "Please enter a Name";
    }

    if (!formValues.movementWeight) {
        errors.movementWeight = "Please enter a Weight";
    }

    return errors;
};

const formWrap = reduxForm({
    form: 'addMovementForm',
    validate: validate,
})(AddPage);

export default connect(mapStateToProps, mapDispatchToProps)(formWrap);
