import React from 'react';
import Header from '../components/Header';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import history from '../history';
//import { getMovements } from '../actions/index';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { useDispatch, useSelector }  from 'react-redux';
import { addMovement } from '../actions';

const useStyles = makeStyles((theme) => ({
    homePageContent: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    movementButtons: {
        fontFamily: 'PT Sans Caption',
        textTransform: 'uppercase',
        marginTop: '100px',
        borderRadius: '10px',
        background: '#C4C4C4',
        width: '70%',
    },
    movementName: {

        fontFamily: 'PT Sans Caption',
        fontSize: '20px', 
        padding: '10px',
    },
    fabDiv: {
        display: 'flex',
        justifyContent: 'flex-end',
        position: 'absolute',
        bottom: theme.spacing(2),
        right: theme.spacing(2),
      },
    fab: {
        background: '#C4C4C4',
    },
}));

const HomePage = () => {
    const classes = useStyles();
    const name  = useSelector(state => state.move.name);
    const weight  = useSelector(state => state.move.weight);
    console.log(name, weight, "inside");

    const displayMovementButtons = () => {
        if (name) {
            return (
                <Button 
                className={classes.movementButtons}
                onClick={() => history.push('/movement/:id')}
            >   
                <div className={classes.movementName} >{name}</div>
            </Button>
            )
        } 

        return <div>Click Add Button to Begin!</div>
    }

    return (
        <div className={classes.homePageContent} >
            <Header title={"Home Page" }/>
            {displayMovementButtons()}
            <div className={classes.fabDiv}>
                <Fab 
                    className={classes.fab}
                    onClick={() => history.push(`/add`)}>  
                    <AddIcon />      
                </Fab>
            </div>
        </div>
    );
};

