import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    updateButton: {
        boxShadow: '0px 2px 2px #A9A9A9',
        background: '#e0e0e0',
        borderRadius: '10px',
        width: '10%',
        textAlign: 'center'
    },
    updateInput: {
        visibility: 'hidden'
    }
}));

const UpdateButton = (props) => {
    const classes = useStyles();

    return (
        <div className={classes.updateButton} id="update" >
            <Button>
                Update
                <div id="targetDiv"> 
                    <input className={classes.updateInput} type="text" name="update 1RM" value="Update"></input>
                </div>
            </Button>
        </div>
    )

};

document.getElementById('myForm').addEventListener('click', function(){
    document.getElementById('targetDiv').style.display = "block";
});

export default UpdateButton;
