import { ADD_MOVEMENT } from '../actions/constants.js';

const intialState = [{
    name: '',
    weight: 0,
}];

const addMovementReducer = (state = intialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT:
            return { ...state, [state.name]: state.name, [action.weight]: action.weight }
        default:
            return state;
    }
};

export default addMovementReducer;
