import { ADD_MOVEMENT } from '../actions/constants.js';

const initialState = [
    {name: '',
    weight: ''}
]

const addMovementReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT: 
            return Object.assign({}, ...state, {name: action.name, weight: action.weight})
        default:
            return state;
    }
};

export default addMovementReducer;
