import React from 'react';
import Header from './Header';
import { makeStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import { createMovement } from '../actions/index';
import { Field, reduxForm } from 'redux-form';
import { useLocation } from 'react-router';

const useStyles = makeStyles(() => ({
    addPage: {
        display: 'flex',
        justifyContent: 'center',
        marginTop: '100px'
    },
    addMovementDiv: {
        background: '#C4C4C4',
        fontFamily: 'PT Sans Caption',
        fontSize: '18px',
        borderRadius: '10px',
        padding: '20px',
        marginTop: '50px',
        display: 'flex',
        flexDirection: 'column',
        textAlign: 'right',
      },
      movementName: {
        padding: '10px',
        margin: '10px',
      },
      textBoxColor: {
        background: 'white',
        borderRadius: '10px',
      },
      movementWeight: {
        margin: '10px',
        padding: '10px',
      },
      addButton: {
          width: '50px',
          borderRadius: '10px',
          marginLeft: '95px'
      },
}));





const renderError = ({ error, touched }) => {
    if (touched && error ) {
        return <div>{error}</div>
    };
};

const MovementForm = (props) => {
    const location = useLocation();
    const pathArray = location.pathname.split('/');
    const movementIDURL = (pathArray[1]);
    const selected = props.move.find((move) => move.id == movementIDURL);
    const selectedID = selected.id
    const classes = useStyles();
    const onSubmit = (formValues) => {
        props.createMovement(formValues);
    };
    const renderInputName = ({ input, label, meta }) => {
        return (
            <div>
                <label>{label}:   </label>
                <input {...input} type="text" value={selected.movementName} /> 
                <div style={{fontSize: "15px", color:"red"}}>{renderError(meta)}</div>
            </div>    
        )  
    };
    const renderInputWeight = ({ input, label, meta }) => {
        return (
            <div>
                <label>{label}:   </label>
                <input {...input} type="number" /> 
                <div style={{fontSize: "15px", color:"red"}}>{renderError(meta)}</div>
            </div>    
        )  
    };


    return (
        <div>
            <Header title={props.title} />
            <div className={classes.addPage}>
                <div className={classes.addMovementDiv}>
                    <form onSubmit={props.handleSubmit(onSubmit)} >
                        <Field 
                            name="movementName"
                            component={renderInputName} 
                            label="Movement Name" 
                        />
                        <Field
                            name="movementWeight" 
                            component={renderInputWeight} 
                            label={props.label} 
                         />
                         <button >Submit</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        formValues: state.move   
    }   
};

const mapDispatchToProps = (dispatch) => {
    return({
        createMovement: (formValues) => dispatch(createMovement(formValues)),
    })
};

const validate = (formValues) => {
    const errors = {};

    if (!formValues.movementName) {
        errors.movementName = "Please enter a Name";
    }

    if (!formValues.movementWeight) {
        errors.movementWeight = "Please enter a Weight";
    }

    return errors;
};

const formWrap = reduxForm({
    form: 'movementForm',
    validate: validate,
})(MovementForm);

export default connect(mapStateToProps, mapDispatchToProps)(formWrap);
