import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import { useLocation } from 'react-router';

const useStyles = makeStyles((theme) => ({
    PREntry: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        fontFamily: 'PT Sans Caption',
        fontSize: '18px',
        marginTop: '100px'
    },
    PRvalue: {
        margin: '10px',
        width: '200px',
    },
    PRChart: {
        background: '#C4C4C4',
        borderRadius: '10px',
        borderCollapse: 'separate',
        borderSpacing: '10px',
        display: 'flex',
        flexDirection: 'column',
    },
    percentHeader: {
        borderRadius: '10px',
        padding: '5px',
        fontSize: '24px', 
        display: 'flex',
        justifyContent: 'space-around'
    },
    percentBox: {
        background: '#E7E7E7',
        border: '1px solid #C4C4C4',
        padding: '5px',
        fontSize: '24px',
        display: 'flex',
        justifyContent: 'space-around'
    },
    notFoundPage: {
        display: 'block',
        fontSize: '30px',
        background: '#C4C4C4',
        padding: '30px',
        boxShadow: '0px 2px 2px #A9A9A9',
        width: '100%'
    },
    notFoundPageClickHere: {
        fontSize: '15px',
    },
    loadingPage: {
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        background: '#C4C4C4',
        boxShadow: '0px 2px 2px #A9A9A9',
        paddingTop: '10px'
    },
    loadingPageText:{
        fontSize: '36px',
        fontFamily: 'PT Sans Caption',
        padding: '30px'
    },
}));

const MovementPage = (props) => {
    const classes = useStyles();
    const location = useLocation();
    const pathArray = location.pathname.split('/');
    const movementNameURL = (pathArray[2]);
    const movementWeightURL = (pathArray[3]);
    const [results, setResults] = useState([]);

    useEffect(() => {
        const arr = [];
        let percentage = 100;
        while (percentage > 50 ) {
            arr.push([percentage, (movementWeightURL * percentage) / 100]);
            percentage -= 5;
        }
        setResults(arr);
    }, [movementWeightURL]);

    return (
        <div>
            <Header title={movementNameURL} />
            <div>
                <div className={classes.PREntry}>One Rep Max: {movementWeightURL}</div>   
                <div className={classes.PRChart}>
                    <h1 className={classes.percentHeader} >Percent   Weight</h1>
                    <div>
                        {results.map((result) => (
                        <div key={result[0]}>
                            <div className={classes.percentBox}>{result[0]}% <div>{result[1]}</div></div>

                        </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {

    return {
        name: state.move,
        weight: state.move
    }
};

 export default connect(mapStateToProps)(MovementPage);
