import { ADD_MOVEMENT } from '../actions/constants.js';

const initialState = {
    name: '',
    weight: ''  
};

const addMovementReducer = (state = initialState, action) => {
    console.log(state);
    switch (action.type) {
        case ADD_MOVEMENT:
            console.log(state, "in");
            return { ...state, name: [action.name], weight: [action.weight] }
        default:
            return state;
    }
};

export default addMovementReducer;
