import { GET_MOVEMENTS } from '../actions/constants.js';

const movementsReducer = (state = {}, action) => {
    switch (action.type) {
        case GET_MOVEMENTS:
            return { ...state, ...action.map(action.name, action.weigth) }
        default:
            return state;
    }
};

export default movementsReducer;
