import { updateSyncErrors } from 'redux-form';
import { ADD_MOVEMENT } from '../actions/constants.js';
import update from 'immutability-helper';

const initialState = {newMovement: []};

const addMovementReducer = (state = initialState, action) => {
    const newMovement = "newMovement";
    switch (action.type) {
        case ADD_MOVEMENT: 
        if (!state[newMovement]) {
            return Object.assign({}, state, { [newMovement]: [action.payload]})
        } else {
            return update(state, {
                [newMovement]: {$push: [action.payload]}
            })
        }
            //return { ...state, [newMovement]: action.payload };
        default:
            return state;
    }
};

export default addMovementReducer;
