import { ADD_MOVEMENT, UPDATE_MOVEMENT } from '../actions/constants.js';

const initialState = [];

const addMovementReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT: 
            return [ ...state, action.payload ];
        case UPDATE_MOVEMENT:
            return [
                // ...state.map(item => Object.assign({}, item, { movementWeight: action.payload.movementWeight }))
                ...state.map(item => item === action.payload ? action.payload : item.movementWeight  )
              ];
        default:
            return state;
    }
};

export default addMovementReducer;
