import { ADD_MOVEMENT } from '../actions/constants.js';

const initialState = {
    name: '',
    weight: null  
};

const addMovementReducer = (state = initialState, action) => {
    const newState = { name: action.name, weight: action.weight }
    switch (action.type) {
        case ADD_MOVEMENT:
            return { ...newState, name: action.name, weight: action.weight }
        default:
            return state;
    }
};

export default addMovementReducer;
