import { UPDATE_MOVEMENT, DELETE_MOVEMENT, CREATE_MOVEMENT } from '../actions/constants.js';

const initialState = [];

const movementReducer = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_MOVEMENT: 
            return [
                ...state, action.payload
            ];
        case UPDATE_MOVEMENT:
            console.log(action.payload.id, "reducer")
            return {...state, [action.payload.id]: action.payload.data}

            // return {
            //     ...state, [action.payload.id]: action.payload
            // };

            // return state.map(item => {
            //     if (item.id === action.payload.id) {
            //         return action.payload.data
            //     } else {
            //         return item
            //     }
            // })


             //return [ ...state.map(item => item.id === action.payload.id ? action.payload.data : item)]


        case DELETE_MOVEMENT:
            return [
                ...state.filter(item => item.movementName !== action.payload.movementName)
            ];
        default:
            return state;
    }
};

export default movementReducer;
