import React from 'react';
import Header from '../components/Header';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import history from '../history';
//import { getMovements } from '../actions/index';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { useSelector }  from 'react-redux';

const useStyles = makeStyles((theme) => ({
    homePageContent: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    noMovementsMessage: {
        marginTop: '130px',
        fontFamily: 'PT Sans Caption',
        fontSize: '20px',
        textTransform: 'uppercase',
    },
    movementButtons: {
        fontFamily: 'PT Sans Caption',
        marginTop: '100px',
        borderRadius: '10px',
        background: '#C4C4C4',
    },
    movementName: {
        fontFamily: 'PT Sans Caption',
        fontSize: '20px', 
        padding: '10px',
    },
    fabDiv: {
        display: 'flex',
        justifyContent: 'flex-end',
        position: 'absolute',
        bottom: theme.spacing(2),
        right: theme.spacing(2),
      },
    fab: {
        background: '#C4C4C4',
    },
}));

const HomePage = () => {
    const classes = useStyles();
    const name  = useSelector(state => state.move.name);

    const displayMovementButtons = () => {
        if (name) {
            return (
                <Button 
                    className={classes.movementButtons}
                    onClick={() => history.push('/movement/:id')}
                >   
                <div className={classes.movementName} >{name}</div>
                </Button>
            )
        } 

        return <div className={classes.noMovementsMessage} >Click add button to begin</div>
    };

    return (
        <div className={classes.homePageContent} >
            <Header title={"Home Page" }/>
            <div className={classes.movementName} >
                <span>{displayMovementButtons()}</span>
            </div>
            <div className={classes.fabDiv}>
                <Fab 
                    className={classes.fab}
                    onClick={() => history.push(`/add`)}>  
                    <AddIcon />      
                </Fab>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        name: state.move.name,  
    }   
};

const withConnect = connect(
    mapStateToProps,
);

export default compose(withConnect)(HomePage);
