import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    updateButton: {
        boxShadow: '0px 2px 2px #A9A9A9',
        background: '#e0e0e0',
        borderRadius: '10px',
        width: '10%',
        textAlign: 'center'
    },
    updateInput: {
        display: 'block',
    }
}));

const UpdateButton = (props) => {
    const classes = useStyles();
    const buttonClicked = () => {
        if (document.getElementById("update").clicked === true) {
            return  <input className={classes.updateInput} type="text" name="update 1RM" value="Update" id="input"></input>
        }

        return null

    }

    return (

            <Button className={classes.updateButton} id="update" onClick={buttonClicked()} >
                Update
            </Button>

    )

};

export default UpdateButton;
