import * as constants from './constants';
import history from '../history';

export const addMovement = (formValues) => {
    history.push('/')
    return {
        type: constants.ADD_MOVEMENT,
        payload: formValues,
    }
};

export const activeMovement = (lift) => {
    history.push(`/movement/${lift.movementName}/${lift.movementWeight}`)
    return {
        type: constants.ACTIVE_MOVEMENT,
        payload: lift,
    }
};
