import React from 'react';
import Header from '../components/Header';
import { makeStyles } from '@material-ui/core/styles';
import { addMovement } from '../actions/index';
import { connect } from 'react-redux';

const useStyles = makeStyles(() => ({
    updatePage: {
        marginTop: '150px',
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'PT Sans Caption',
    }
}));

const UpdatePage = (props, { newWeigth, addMovement }) => {
    const classes = useStyles();
    const objIndex = props.weight.findIndex((obj) => obj.movementName === "bench");
    const newObjIndex = props.weight[objIndex].movementName = "Squat"

    const onSubmit = () => {
        return newObjIndex
    }
    return (
        <div>
            <Header title="Update Movement" />
            <div className={classes.updatePage}>
                <form onSubmit={props.handleSubmit(onSubmit)}>
                    <label>New One Rep Max: </label>
                    <input 
                        type="number" 
                        id="newWeight"
                        name="updateInput"
                        placeholder="Enter new weight"
                    ></input>
                    <button>Submit</button>
                </form>
            </div>
        </div>
    );
};

const mapStateToProps = state => {
    return {
      weight: state.move
    };
  };
  const mapDispatchToProps = {
    addMovement
  };
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(UpdatePage);
