import * as constants from './constants';
import history from '../history';

export const getMovements = (name, weight) => {
    return {
        type: constants.GET_MOVEMENTS,
        name,
        weight
    }
};

export const addMovement = (name, weight) => {
    history.push('/')
    return {
        type: constants.ADD_MOVEMENT,
        name, 
        weight,
    }
};
