import { ADD_MOVEMENT } from '../actions/constants.js';
import initialState  from './initialState';

const getMovementsReducer = (state = initialState , action) => {
    switch (action.type) {
        case ADD_MOVEMENT:
            return { ...state, name: action.name }
        default:
            return state;
    }
};

export default getMovementsReducer;

