import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useLocation } from 'react-router';
import { connect } from 'react-redux';

const useStyles = makeStyles(() => ({
    oneRepMaxWeight: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        fontFamily: 'PT Sans Caption',
        fontSize: '18px',
        marginTop: '100px'
    },
    oneRepMaxChart: {
        background: '#C4C4C4',
        borderRadius: '10px',
        borderCollapse: 'separate',
        borderSpacing: '10px',
        display: 'flex',
        flexDirection: 'column',
    },
    chartHeader: {
        borderRadius: '10px',
        padding: '5px',
        fontSize: '24px', 
        display: 'flex',
        justifyContent: 'space-around'
    },
    chartBox: {
        background: '#E7E7E7',
        border: '1px solid #C4C4C4',
        padding: '5px',
        fontSize: '24px',
        display: 'flex',
        justifyContent: 'space-around',
        fontFamily: 'PT Sans Caption',
    },
}));

const PercentChart = (props) => {
    const classes = useStyles();
    const location = useLocation();
    const pathArray = location.pathname.split('/');
    const movementNameURL = (pathArray[2]);
    console.log(movementNameURL, "url");
    const [results, setResults] = useState([]);
    // const mapWeights = props.weight.map((value) => {
    //     return value.movementWeight


    // });

    const mapWeights = props.weight.map(value => parseInt(value.movementWeight));
    console.log(mapWeights, "Map");
    console.log(props.weight, "props");

    const percentValues = [100, 95, 90, 85, 80, 75, 70, 65, 60, 55];

    const computePercentages = (movementWeight) =>
        percentValues.map((percent) => (movementWeight * percent) / 100);

    const percentages = props.weight.map((el) => {
        return computePercentages(el.movementWeight)
    });

    console.log(percentages, "perc");

    const dataWithPercentages = props.weight.map((el) => {
        return {
          ...el,
          percentages: computePercentages(el.movementWeight),
        };
      });

      const perc = dataWithPercentages[0].percentages
      console.log(perc, "pp");

      console.log(dataWithPercentages, "datawith");

       const selected = props.weight.find((e) => {
        if (e.movementName === movementNameURL) {
            return e.movementWeight
        }
        return e
       });
       console.log("Weight", selected.movementWeight);
       console.log("Percentages", selected.percentages);

    // useEffect(() => {
    //     const arr = [];
    //     let percentage = 100;
    //     while (percentage > 50 ) {
    //         arr.push([percentage, (mapWeights * percentage) / 100]);
    //         percentage -= 5;
    //     }
    //     setResults(arr);
    //     // eslint-disable-next-line react-hooks/exhaustive-deps
    // }, []);

    console.log(results, "results");

    const displayWeight = () => {
        if (mapWeights.length === 0) {    
            return <div>No 1RM recorded</div> 
        };

        return <div>{mapWeights}</div>
    };

    return (
        <div>
            <div className={classes.oneRepMaxWeight}>One Rep Max: {displayWeight()}</div>   
            <div className={classes.oneRepMaxChart}>
                <h1 className={classes.chartHeader} >Percent<div>Weight</div></h1>
                <div>
                    {results.map((result) => (
                    <div key={result[0]} className={classes.chartBox} >{result[0]}% <div>{result[1]}</div></div>
                    ))}
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        weight: state.move,
    }
};

export default connect(mapStateToProps)(PercentChart);
