import { ADD_MOVEMENT, UPDATE_MOVEMENT } from '../actions/constants.js';

const initialState = [];

const addMovementReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT: 
            return [ ...state, action.payload ];
        case UPDATE_MOVEMENT:
            return [

                ...state.map(item => Object.assign({}, item, { active: false }))
              ];
        default:
            return state;
    }
};

export default addMovementReducer;
