import { ADD_MOVEMENT } from '../actions/constants.js';

const initialState = {
    movement: [
        {
            name: '',
            weight: 0
        }
    ]
};

const addMovementReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MOVEMENT:
            return { ...state, movement: [ ...state.movement, { name: action.name, weight: action.weight} ] }
        default:
            return state;
    }
};

export default addMovementReducer;
